import { Component, OnInit } from '@angular/core';
import { FaceSnap } from '../models/face-snap.model';
import { FaceSnapsService } from '../services/face-snaps.service';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-single-face-snap',
  templateUrl: './single-face-snap.component.html',
  styleUrls: ['./single-face-snap.component.scss'],
})
export class SingleFaceSnapComponent implements OnInit {
  // Le ! ou bang permet de promettre l'initlisaiton √† typescript
  // @Input est un d√©corateur qui permet comme les props en React de dire au component parent qu'il peut recevoir des info du parent
  // Ici on r√©cup√®re la structure de FaceSanp que l'on peut retrouver dans les models, √ßa √©vite de devoir recr√©er √† chaque fois la structure qui ne change pas
  // Input a retirer aussi car on ne veut plus lui passer la propri√©t√© mais passer par le service
  // @Input() faceSnap!: FaceSnap;
  faceSnap!: FaceSnap;
  faceSnap$!: Observable<FaceSnap>;
  isSnapped!: boolean;
  buttonText!: string;

  //Permet de tester la methode likeFaceSnapById()
  // Il faut l'importer ici
  constructor(
    private faceSnapsService: FaceSnapsService,
    // J'ai besoin d'injecter dans mon component la route active, pour recuperer l'id du faceSnap
    //Permet de recuperer dans ngOnInit faceSnapId car on ne peut pas passer par @input qu'on du coup supprim√©
    private route: ActivatedRoute
  ) {}

  //Appeler √† l'initialisation du component
  ngOnInit() {
    this.prepareInterface();
    this.getFaceSnap();
  }

  onLike() {
    if (this.buttonText === 'Like !') {
      this.faceSnapsService.likeFaceSnapById(this.faceSnap.id, 'like');
      this.buttonText = 'Unlike !';
      //Provient maintenant de la methode dans le service, s'il est laiss√© √ßa double les vote
      // this.faceSnap.snaps++;
    } else {
      this.faceSnapsService.likeFaceSnapById(this.faceSnap.id, 'unlike');
      this.buttonText = 'Like !';
      // this.faceSnap.snaps--;
    }
  }

  //Il est d'usage de mettre les methode private √† la fin
  private getFaceSnap() {
    // Une route renvoie une string du coup pour faire matcher ce qu'on recupere en params et m'id du snap il faut le convertir
    const faceSnapId = parseInt(this.route.snapshot.params['id']);
    // const faceSnapId = this.route.snapshot.params['id'];
    this.faceSnap$ = this.faceSnapsService.getFaceSnapById(faceSnapId);
  }

  //Refactorisation de cette partie qui se trouvait dans ngOnInit
  //Ca permet un code maintenable et lisible
  private prepareInterface() {
    this.buttonText = 'Like !';
    this.isSnapped = false;
  }
}
<main>
  <p>Je suis single facesnap component</p>
  <!-- SI on ne met pas le / avant facesnaps il va essayer d'ajouter le mot facesnaps √† la route actuelle -->
  <a class="btBack" [routerLink]="['/facesnaps']">Back</a>
  <div
    *ngIf="faceSnap$ | async as faceSnap"
    class="face-snap-card"
    [ngClass]="{ snapped: buttonText === 'Unlike !' }"
  >
    <h2>{{ faceSnap.title | uppercase }}</h2>
    <p>Mise en ligne le {{ faceSnap.createdDate | date : "longDate" }}</p>
    <img [src]="faceSnap.imageUrl" [alt]="faceSnap.title" />
    <p>{{ faceSnap.description | titlecase }}</p>

    <!-- Condition pass√©e pour la location-->
    <!-- Sans condition test juste si true  -->
    <!-- <p *ngIf = "faceSnap.location">Photo prise √† {{ faceSnap.location }}</p> -->
    <p *ngIf="faceSnap.location === 'Paris'">
      Photo prise √† {{ faceSnap.location }}
    </p>
    <!-- <p>{{ 4346234.36 | number : "1.0-0" }}</p>
    <p>{{ 4346234.36 | number : "1.0-1" }}</p>
    <p>{{ 0.336 | percent }}</p>
    <p>{{ 0.336 | percent : "1.0-1" }}</p>
    <p>{{ 344.46 | currency }}</p>
    <p>{{ 344.46 | currency : "EUR" }}</p>
    <p>{{ 344.46 | currency : "EUR" : "code" }}</p> -->
    <p>
      <button (click)="onLike()">{{ buttonText }}</button>
      ü§å
      <span [ngStyle]="{ color: 'rgb(0, ' + faceSnap.snaps + ', 0)' }">{{
        faceSnap.snaps
      }}</span>
    </p>
  </div>
</main>
